  def self.import
    INSTA[:photos].each do |line|
      unless Post.where(published_at: line[:taken_at]).any?
        post = Post.create!(content: line[:caption], design: 'insta',
          published_at: line[:taken_at], published: true)
        post.labels << Label.find_by(name: 'Niels Workshop')
        post.labels << Label.find_by(name: 'english')
      end
      #break
    end
  end
end